/*global describe, beforeEach, it*/
'use strict';

var mocha = require('mocha'),
  assert = require('chai').assert,
  httpConfig = require('../src/http-config');

describe('http-config', function () {

  it('should have a hostname', function (done) {
    assert.property(httpConfig, 'hostname');
    assert.isNotNull(httpConfig.hostname);
    assert.notEqual(httpConfig.hostname, '');
    done();
  });

  it('should have a numeric port', function (done) {
    assert.property(httpConfig, 'port');
    assert.isNotNull(httpConfig.port);
    assert.isNumber(httpConfig.port);
    done();
  });

  it('should have a path', function (done) {
    assert.property(httpConfig, 'path');
    assert.isNotNull(httpConfig.path);
    assert.notEqual(httpConfig.path, '');
    done();
  });

  it('should have a method', function (done) {
    assert.property(httpConfig, 'method');
    assert.isNotNull(httpConfig.method);
    assert.notEqual(httpConfig.method, '');
    done();
  });

  it('should have a user agent', function (done) {
    assert.property(httpConfig, 'headers');
    assert.property(httpConfig.headers, 'User-Agent');
    var userAgent = httpConfig.headers['User-Agent'];
    assert.isNotNull(userAgent);
    assert.notEqual(userAgent, '');
    done();
  });
});